import React, { Component }  from 'react';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import PatientForm from "./components/patient/PatientForm";
import RecordView from "./components/doctor/RecordView";
import ChatBot from 'react-simple-chatbot';
import Button from "./components/doctor/Button";

const ChatSteps = [
    {
        id: '0',
        message: 'Hello Welcome',
        trigger: '1',
    },{
        id: '1',
        message: 'Please enter legal name:',
        trigger: '2'
      },{

        id: '2',
        user: true,
        trigger: '3',
      },
      {
        id: '3',
        message: "hi {previousValue}, how may I help you?",
        trigger: '4',
      },{

        id: '4',
        message: "what's up",
        end: true
      }

];
 
class App extends Component{
  render(){
    return (

        <div className="Chatbot">
            <h1>Welcome to HealthBot</h1>

            <p>
            Click Here to View Records (Doctor Use Only)
            </p>
            <Router>
              <Button to = "" />
              <Butoon to = "records" />
              <Routes>
                <Route path="/" element = {<App />} />
                <Route path="/records" element={<RecordView />} />
              </Routes>

            </Router>


            <PatientForm />
        </div>

    );
}
}
 


export default App;






