import axios from 'axios';
import React, { Component } from 'react';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import UpdateLink from "./UpdateLink"

let axiosConfig = {
    headers: {
        'Content-Type': 'application/json',

    }
  };

//get user input from the chatbot interaction
class RecordView extends Component{

    constructor(props){
        super(props);
        this.state = {
            recordsList: []
        }
    }


componentDidMount(){

    //send the HTTP GET request
    this.refreshRecords();

}

refreshRecords = () => {
    axios.get("/api/chats/")
    .then((res) => this.setState({recordsList: res.data }));

};

renderRecords = () => {

    const items = this.state.recordsList;

    return items.map((item) => (
        <tr key = {item.id}>
        <td>{item.id}</td>
        <td>{item.patient_id}</td>
        <td>{item.patient_name}</td>
        <td>{item.email}</td>
        <td>{item.dob}</td>
        <td>{item.symptoms}</td>
        <td>{item.diagnosis}</td>
        <td>link to {item.id}</td>
        </tr>
    ));
};


render(){
    return(

        <div>
        <h1>Patient Records</h1>
        <table id="patientrecords">
            <tr>
                <th>Record ID</th>
                <th>Patient ID</th>
                <th>Patient Name</th>
                <th>Email</th>
                <th>DOB</th>
                <th>Symptoms</th>
                <th>Diagnosis</th>
                <th>Update/Edit</th>

            </tr>
            {this.renderRecords()}

        </table>

        </div>



    )
    }
};
