import axios from 'axios';
import React, { Component } from 'react';
let axiosConfig = {
    headers: {
        'Content-Type': 'application/json',
        "Vary": "Accept",
         "Data": { csrfmiddlewaretoken: "{{ csrf_token }}",   // < here 
            state:"inactive" 
          }
    }
  };
//get user input from the chatbot interaction
class PostRequest extends Component{
    constructor(props){
        super(props);
        const {steps} = this.props;
        const { patientname, patientemail,patientdob, symptoms} = steps;
        this.state = { patientname, patientemail,patientdob, symptoms};

    }


componentDidMount(){
    const patientRequest = {
        patient_name: this.state.patientname.value,
        email: this.state.patientemail.value,
        dob: this.state.patientdob.value,
        symptoms: this.state.symptoms.value

    };
    //send the HTTP POST, update the database
    axios.post('/api/chats/',patientRequest,axiosConfig)
    .then(
        res=>console.log(res)
    )
}

render(){
    return(
        <div>Thank you for submitting a request. We will contact you shortly.</div>
    )
    }
};
export default PostRequest;
