import { render } from '@testing-library/react';
import axios from 'axios';
import React from 'react';
let axiosConfig = {
    headers: {
        'Content-Type': 'application/json',

    }
  };

  class UpdateRecord extends Component {

        constructor(props){
            super(props);
            this.state = {
                record: []
            }
        }


    componentDidMount(){

        //get the specific record
        this.retriveRecord();

    }

    retriveRecord = () => {
        axios.get("/api/chats/1")
        .then((res) => this.setState({record: res.data }));

    };


    render(){
        return(
        <div className="update">
            <h1>Update Record</h1>
            <form>
                    <h5>Record {recordid}</h5>

                    <label for="patientid">Patient ID</label><br></br>
                     <input type="text" id="patientid" name="patientid" value="id" readonly></input><br></br>
                     <label for="patientname">Patient Name</label><br></br>
                     <input type="text" id="patientname" name="patientname" value="name" readonly></input><br></br>

                    <label for="email">Email</label><br></br>
                     <input type="text" id="email" name="email" value="email" readonly></input><br></br>
                     <label for="dob">DOB</label><br></br>
                     <input type="text" id="dob" name="dob" value="2000-12-03" readonly></input><br></br>

                    <label for="symptoms">Symptoms</label><br></br>
                     <input type="text" id="symptoms" name="symptoms" value="sick" readonly></input><br></br><br></br>
                     Diagnosis: <input type="text" id="diagnosis" name="diagnosis" ></input><br></br>


            </form>

        </div>
    )
    }
};

export default UpdateRecord;
