import React, {Component} from 'react';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';
import PostRequest from './PostRequest';

const style = {
    background :'#e9f7f1',
    fontFamily: 'Georgia',
    botBubbleColor: '#000000',
    botFontColor: '#fff',
    userBubbleColor: '#e3bc52',


};
const ChatSteps = [
        {
            id: 'intro',
            message: 'Hello.',
            trigger: 'agreement',
        },{
             id: 'agreement',
            message: 'Would you like to submit a patient reqeust?',
            trigger: 'intro-patient'
        },{
            id:'intro-patient',
            options:[
                {value:'y',label: "Yes",trigger:"request-name"},
                {value:'n',label: "No", trigger:"end-chat"},
            ]
        },
        {
            id:'end-chat',
            message: 'No worries, have a great day',
            end: true,
        },
        {
            id: 'request-name',
            message: 'Please enter your legal name:',
            trigger: 'patientname'
          },{

            id: 'patientname',
            user: true,
            validator: (value)=> {
                if(/^[A-Za-z][A-Za-z\'\-]+([\ A-Za-z][A-Za-z\'\-]+)*/.test(value)){
                    return true;
                }
                else{
                    return 'Please enter valid name.';
                }
            },
            trigger: 'welcome-name',
          },
          {
            id: 'welcome-name',
            message: "Welcome {previousValue}. Let's create your request",
            trigger: 'request-email',
          },{
                id:'request-email',
                message: "Please enter your email:",
                trigger:"patientemail",
          },
          {
            id: 'patientemail',
            user:true,
            validator: (value) => {
                if(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value)){
                    return true;
                }
                else{
                    return 'Please enter a valid email';
                }
            },
            trigger:'request-dob',
          },
          {
              id:'request-dob',
              message:'Please enter your DOB:(YYYY-MM-DD)',
              trigger:'patientdob',
          },
          {
              id:'patientdob',
              user:true,
              validator: (value) => {
                  if(/^\d{4}\-(0[1-9]|1[012])\-(0[1-9]|[12][0-9]|3[01])$/.test(value)){
                      return true;
                  }
                  else{
                      return 'Please enter valid DOB (YYYY-MM-DD)'
                  }
              },
              trigger:'request-symptoms',

          },
          {
            id:'request-symptoms',
            message:'What are your symptoms?',
            trigger:'symptoms',
          },
          {
              id:'symptoms',
            options:[
                {value:'cough',label:'Cough',trigger:'send-request'},
                {value:'fever',label:'Fever',trigger:'send-request'},
                {value:'headache',label:'Headache',trigger:'send-request'},

            ]
          },
          {
              id:'send-request',
              component: <PostRequest />,
              asMessage:true,
              end: true
          }


    ];
class PatientForm extends Component{
    render(){
        return(
            <ThemeProvider theme={style}>
             <ChatBot steps={ChatSteps} />
            </ThemeProvider>
        )  
    }
}

export default PatientForm;
