import axios from 'axios';
import React, { Component } from 'react';
let axiosConfig = {
    headers: {
        'Content-Type': 'application/json',

    }
  };

//get user input from the chatbot interaction
class RecordView extends Component{

    constructor(props){
        super(props);
        this.state = {
            recordsList: []
        }
    }


componentDidMount(){

    //send the HTTP GET request
    this.refreshRecords();

}

refreshRecords = () => {
    axios.get("/api/chats/")
    .then((res) => this.setState({recordsList: res.data }));

};

renderRecords = () => {

    const items = this.state.recordsList;

    return items.map((item) => (
        <li
        key = {item.id}
        name = {item.name}
        >
        </li>
    ));
};

render(){
    return(

        <div>
        <h1>Patient Records</h1>
        <ul>{this.renderRecords()}</ul>
        </div>



    )
    }
};
