import React, {Component} from 'react';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';

const style = {
    background :'#e9f7f1',
    fontFamily: 'Copperplate',
    botBubbleColor: '#e27aff'

};
const ChatSteps = [
        {
            id: 'intro',
            message: 'Hello.',
            trigger: 'agreement',
        },{
             id: 'agreement',
            message: 'Would you like to submit a patient reqeust?',
            trigger: 'intro-patient'
        },{
            id:'intro-patient',
            options:[
                {value:'y',label: "Yes",trigger:"request-name"},
                {value:'n',label: "No", trigger:"end-chat"},
            ]
        },
        {
            id:'end-chat',
            message: 'No worries, have a great day',
            end: true,
        },
        {
            id: 'request-name',
            message: 'Please enter your legal name:',
            trigger: 'patient-name'
          },{

            id: 'patient-name',
            user: true,
            validator: (value)=> {
                if(/^[A-Za-z][A-Za-z\'\-]+([\ A-Za-z][A-Za-z\'\-]+)*/.test(value)){
                    return true;
                }
                else{
                    return 'Please enter valid name.';
                }
            },
            trigger: 'welcome-name',
          },
          {
            id: 'welcome-name',
            message: "Welcome {previousValue}. Let's create your request",
            trigger: 'request-email',
          },{
                id:'request-email',
                message: "Please enter your email:",
                trigger:"patient-email",
          },
          {
            id: 'patient-email',
            user:true,
            validator: (value) => {
                if(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value)){
                    return true;
                }
                else{
                    return 'Please enter a valid email';
                }
            },
            trigger:'request-dob',
          },
          {
              id:'request-dob',
              message:'Please enter your DOB:(mm/dd/yyyy)',
              trigger:'patient-dob',
          },
          {
              id:'patient-dob',
              user:true,
              validator: (value) => {
                  if(/^(0[1-9]|1[0-2])\/(0[1-9]|1\d|2\d|3[01])\/(19|20)\d{2}$/.test(value)){
                      return true;
                  }
                  else{
                      return 'Please enter valid DOB (mm/dd/yyyy)'
                  }
              },
              trigger:'request-symptoms',

          },
          {
            id:'request-symptoms',
            message:'What are your symptoms?',
            trigger:'symptom-options',
          },
          {
              id:'symptom-options',
            options:[
                {value:'cough',label:'Cough',trigger:'send-email'},
                {value:'fever',label:'Fever',trigger:'send-email'},
                {value:'headache',label:'Headache',trigger:'send-email'},

            ]
          },
          {
              id:'send-email',
              message: 'Thank you for your request, we will contact you shortly',
              end: true
          }


    ];
class PatientForm extends Component{
    render(){
        return(
            <ThemeProvider theme={style}>
             <ChatBot steps={ChatSteps} />
            </ThemeProvider>
        )  
    }
}

export default PatientForm;
